import React, { useState } from 'react'
import './input.css'
import { AiFillEye,AiFillEyeInvisible } from "react-icons/ai";



export const Input = ({label,placeholder,onChange,style,showLabel=true},type='text') => {
  return (
    <div className='input-container'>
        {showLabel && <label>{label}</label>}
        <input type={type} className='input' placeholder={placeholder} onChange={onChange} style={style}></input>
    </div>
  )
}

export const PasswordInput = ({label,placeholder,onChange,style,showLabel=true}) => {
    const [show,setShow]= useState(false)
    return (
      <div className='input-container'>
          {showLabel && <label>{label}</label>}
          <div>
          <input className='input' type={show? 'text': 'password'} placeholder={placeholder} onChange={onChange} style={style}></input>
          <div className='pass-icon'>
          {show ? <AiFillEye/> : <AiFillEyeInvisible/>}
          </div>
          </div>

      </div>
    )
  }
