import React from 'react'
import './input.css'

export const Input = ({label,placeholder,onChange,style,showLabel=true}) => {
  return (
    <div className='input-container'>
        {showLabel && <label>{label}</label>}
        <input className='input' placeholder={placeholder} onChange={onChange} style={style}></input>
    </div>
  )
}
